    def IsValidPos(self, x, y):
            if(0 <= x < self.max_x and 0 <= y < self.max_y and self.maze[y][x] != Tiles.Wall):
                return True
            else:
                return False 
    def PutCoins(self):
        self.coins.clear()
        for y, row in enumerate(self.maze):
            for x,cell in enumerate(row):
                if cell == Tiles.Empty or cell == Tiles.Coin:
                    if random.random() < 0.9915:
                        self.maze[y][x] = Tiles.Empty
                        continue
                    else:
                        self.maze[y][x] = Tiles.Coin
                        self.coins.add((y,x))
    def PutSlime(self):    
        for y, row in enumerate(self.maze):
            for x,cell in enumerate(row):
                if cell == Tiles.Empty or cell == Tiles.Slime:
                    if random.random() < 0.975:
                        self.maze[y][x] = Tiles.Empty
                        continue
                    else:
                        self.maze[y][x] = Tiles.Slime

    def PrintMaze(self):

        print(Colors.Red * (self.max_x + 2)) 
        for row in self.maze:
            print(Colors.Red, end='')
            for cell in row:
                if cell == Tiles.Wall: 
                    print('██', end='')
                elif cell == Tiles.Slime: 
                    print(Colors.Purple, end='')
                elif cell == Tiles.Coin: 
                    print(Colors.Yellow, end='')
                else: print('  ', end='')
            print(Colors.Red)

        print(Colors.Red * (self.max_x + 2))
    def PathCost(self, path):
        Cost = 0
        for x,y in path:
            if self.maze[y][x] == Tiles.Empty:
                Cost += 1
            elif self.maze[y][x] == Tiles.Slime:
                Cost += 30
        
        return Cost
    def PrintPath(self, path):
        T, path = path
        if not path:
            print("No path to print.")
            return
        else:
            print(Colors.Red * (self.max_x + 2)) 
            for y, row in enumerate(self.maze):
                print(Colors.Red, end='')
                for x, cell in enumerate(row):
                    if (x, y) in path and (y, x) in self.coins: 
                        print(Colors.Green, end='')
                    elif (x, y) in path and cell == Tiles.Slime : 
                        print(Colors.Red, end='')
                    elif (x, y) in path: 
                        print(Colors.Blue, end='')
                    elif cell == Tiles.Wall:
                        print('██', end='')
                    elif cell == Tiles.Coin:
                        print(Colors.Yellow, end='')
                    elif cell == Tiles.Slime:
                        print(Colors.Purple, end='')
                    else: 
                        print('  ', end='')
                print(Colors.Red)

        print(Colors.Red * (self.max_x + 2))
        print(f"Effort:   {self.PathCost(path)}")

    def FindCoins(self):
        coins = []
        for i in range(self.max_y):
            for j in range(self.max_x):
                if self.maze[i][j] == Tiles.Coin:
                    coins.append((i, j))

        return set(coins)
    def ZeroCounter(self):
        counter = 0
        for row in self.maze:
            for cell in row:
                if cell == Tiles.Empty:
                    counter +=1
        print(counter)

